import React, { useState } from 'react';
import Lightbox from 'react-image-lightbox';
import 'react-image-lightbox/style.css'; // Lightbox CSS

const images = [
  'data:image/jpeg;base64,',
  'data:image/jpeg;base64,',
  'data:image/jpeg;base64,',
  'data:image/jpeg;base64,',
  'data:image/jpeg;base64,',
  'data:image/jpeg;base64,',
  'data:image/jpeg;base64,',
  'data:image/jpeg;base64,',
];

// Descriptions & titles for each image
const descriptions = [
  { title: "Hospital Bed", desc: "Comfortable and equipped with the latest technology." },
  { title: "Medical Consultation", desc: "Personalized consultations with top doctors." },
  { title: "Emergency Room", desc: "State-of-the-art emergency services for urgent care." },
  { title: "Healthcare Facility", desc: "A clean, well-lit environment designed for your well-being." },
  { title: "Telemedicine", desc: "Consultations through virtual appointments." },
  { title: "Hospital Interior", desc: "A welcoming atmosphere to make you feel at ease." },
  { title: "Medical Equipment", desc: "Cutting-edge medical tools for precise treatments." },
  { title: "Patient Recovery", desc: "Dedicated recovery zones for a quick recovery." },
];

const Gallery = () => {
  const [photoIndex, setPhotoIndex] = useState(0);
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      <section style={{ backgroundColor: '#e0f7fa', padding: '60px 0' }}>
        <div className="container">
          <div className="row justify-content-center mb-5">
            <div className="col-lg-8 text-center">
              <div className="section-title">
                <div
                  className="divider mb-3"
                  style={{
                    width: '60px',
                    height: '3px',
                    backgroundColor: '#4CAF50', // Hospital green color
                    margin: '0 auto 20px auto',
                    borderRadius: '2px',
                  }}
                ></div>
                <h2 style={{ color: "#004d4d", fontWeight: '700', letterSpacing: '2px', textTransform: 'uppercase' }}>
                  Our Hospital Gallery
                </h2>
                <p style={{ color: '#555', fontSize: '1.1rem' }}>
                  Explore our state-of-the-art facilities and medical care services.
                </p>
              </div>
            </div>
          </div>

          <div className="row no-gutters">
            {images.map((img, idx) => (
              <div
                key={idx}
                className="col-lg-3 col-md-4 col-sm-6 p-1"
                style={{
                  cursor: 'pointer',
                  overflow: 'hidden',
                  borderRadius: '12px',
                  position: 'relative',
                  boxShadow: '0 4px 15px rgba(72, 191, 87, 0.3)', // Greenish shadow
                }}
                onClick={() => {
                  setPhotoIndex(idx);
                  setIsOpen(true);
                }}
              >
                <img
                  src={img}
                  alt={`Gallery Image ${idx + 1}`}
                  style={{
                    width: '100%',
                    height: '220px',
                    objectFit: 'cover',
                    borderRadius: '12px',
                    transition: 'transform 0.3s ease, filter 0.3s ease',
                    boxShadow: '0 4px 15px rgba(72, 191, 87, 0.3)',
                    display: 'block',
                  }}
                  onMouseEnter={e => {
                    e.currentTarget.style.transform = 'scale(1.05)';
                    e.currentTarget.style.filter = 'brightness(1.1)';
                  }}
                  onMouseLeave={e => {
                    e.currentTarget.style.transform = 'scale(1)';
                    e.currentTarget.style.filter = 'brightness(1)';
                  }}
                />
                {/* Centered Title Overlay */}
                <div
                  style={{
                    position: 'absolute',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                    color: "#004d4d",
                    backgroundColor: 'rgba(0, 0, 0, 0.6)',
                    padding: '6px 12px',
                    borderRadius: '8px',
                    fontWeight: '700',
                    fontSize: '14px',
                    pointerEvents: 'none',
                    userSelect: 'none',
                    textAlign: 'center',
                    width: '90%',
                    whiteSpace: 'nowrap',
                    textOverflow: 'ellipsis',
                    overflow: 'hidden',
                    letterSpacing: '1px',
                    textTransform: 'uppercase',
                  }}
                >
                  {descriptions[idx].title}
                </div>
                {/* Click Here overlay */}
                <div
                  style={{
                    position: 'absolute',
                    bottom: '10px',
                    right: '10px',
                    backgroundColor: "#004d4d", // Green button
                    color: '#fff',
                    fontWeight: '700',
                    fontSize: '12px',
                    padding: '4px 8px',
                    borderRadius: '10px',
                    userSelect: 'none',
                    pointerEvents: 'none',
                    letterSpacing: '1px',
                    textTransform: 'uppercase',
                  }}
                >
                  Click Here
                </div>
              </div>
            ))}
          </div>

          {isOpen && (
            <Lightbox
              mainSrc={images[photoIndex]}
              nextSrc={images[(photoIndex + 1) % images.length]}
              prevSrc={images[(photoIndex + images.length - 1) % images.length]}
              onCloseRequest={() => setIsOpen(false)}
              onMovePrevRequest={() =>
                setPhotoIndex((photoIndex + images.length - 1) % images.length)
              }
              onMoveNextRequest={() => setPhotoIndex((photoIndex + 1) % images.length)}
              imageCaption={
                <div style={{ textAlign: 'center', color: '#FFD700', lineHeight: '1.3' }}>
                  <h3 style={{ margin: 0, fontWeight: '700' }}>{descriptions[photoIndex].title}</h3>
                  <p style={{ margin: '6px 0 0' }}>{descriptions[photoIndex].desc}</p>
                </div>
              }
              enableZoom={false}
              reactModalStyle={{ overlay: { zIndex: 1500 } }}
            />
          )}
        </div>
      </section>
    </>
  );
};

export default Gallery;
