import React from 'react';

const getCardStyle = (imageUrl) => ({
  background: `url(${imageUrl}) center/cover no-repeat`,
  color: 'white',
  borderRadius: '8px',
  padding: '2.5rem',
  border: 'none',
  minHeight: '350px',
  position: 'relative',
  overflow: 'hidden',
  transition: 'transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
  boxShadow: '0 6px 12px rgba(0, 0, 0, 0.25)',
});

const overlayStyle = {
  position: 'absolute',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  zIndex: 0,
};

const zoomStyle = {
  transform: 'scale(1.09)',
  boxShadow: '0 12px 24px rgba(0, 0, 0, 0.4)',
};

const cardContainerStyle = {
  position: 'relative',
  zIndex: 1,
};

const linkStyle = {
  color: '#FFD700',
  textTransform: 'uppercase',
  fontSize: '13px',
  letterSpacing: '1px',
  fontWeight: 'bold',
  zIndex: 2,
  position: 'relative',
};

const Introduction = () => {
  const handleMouseEnter = (e) => {
    Object.assign(e.currentTarget.style, zoomStyle);
  };

  const handleMouseLeave = (e) => {
    e.currentTarget.style.transform = 'scale(1)';
    e.currentTarget.style.boxShadow = getCardStyle('').boxShadow;
  };

  const cards = [
    {
      title: "24/7 Emergency Care",
      text: "Our hospital provides round-the-clock emergency services, ensuring immediate care when you need it most.",
      number: "01",
      image: "data:image/jpeg;base64," // Emergency Room Image
    },
    {
      title: "Personalized Health Plans",
      text: "Get a tailored healthcare plan designed to meet your individual medical needs, ensuring the best outcomes.",
      number: "02",
      image: "data:image/jpeg;base64," // Consultation Image
    },
    {
      title: "Advanced Medical Technology",
      text: "Our hospital uses the latest medical technology for precise diagnostics and treatments, giving you the best care possible.",
      number: "03",
      image: "data:image/jpeg;base64," // Medical Equipment Image
    }
  ];

  return (
    <section className="mt-80px">
      <div className="container">
        <div className="row">
          {cards.map((item, index) => (
            <div key={index} className="col-lg-4 col-md-6 mb-4">
              <div
                style={getCardStyle(item.image)}
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
              >
                <div style={overlayStyle}></div>

                <div style={cardContainerStyle}>
                  <span className="number" style={{ fontSize: '2rem', color: '#FFD700' }}>{item.number}</span>
                  <h3 className="mt-3" style={{ color: '#ffffff' }}>{item.title}</h3>
                  <p className="mt-3 mb-4" style={{ color: '#e0e0e0' }}>{item.text}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Introduction;
